import React,{useState} from 'react'
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup"
import axios from "axios"
import { useNavigate } from "react-router-dom";

function Login() {
  let na
 const[username,setUsername]=useState("")
 const[password,setPassword]=useState("")
  const login=()=>{
    const data={username:username,password:password}
    axios.post("http://localhost:3001/auth/login",data).then((response)=>{
      console.log(response.data)
      if(response.data.error) alert(response.data.error)
      else sessionStorage.setItem("accessToken",response.data)
    })
  }
  return (
    <div className='loginContainer'>
      <input type="text" autoComplete='off' onChange={(event)=>{setUsername(event.target.value)}} name="username"  />
      <input type="password" onChange={(event)=>{setPassword(event.target.value)}} name="password"  />
    <button onClick={login} type='submit'>Login</button>

    </div>
  )
}

export default Login
