import React, { useEffect } from 'react'
import { useParams } from 'react-router-dom'
import axios from 'axios'
function Post() {
  let { id } = useParams()
  useEffect(() => {
    axios.get(`http://localhost:3001/posts/byId/${id}`).then((response) => {
      setPostObject(response.data)
    });
  })
  return (
    <div>Post {id}</div>
  )
}

export default Post
